import parseNDJSON from './ndjson-parser';

// Helper: build a ReadableStream<Uint8Array> that emits given string chunks
const te = new TextEncoder();
function streamFromStrings(chunks) {
  return new ReadableStream({
    start(controller) {
      let i = 0;
      const pump = () => {
        if (i >= chunks.length) { controller.close(); return; }
        controller.enqueue(te.encode(chunks[i++]));
        setTimeout(pump, 0); // async to exercise the reader loop
      };
      pump();
    }
  });
}

function expectEqual(a, b){ expect(JSON.stringify(a)).toEqual(JSON.stringify(b)); }

describe('Streaming NDJSON Parser', () => {
  test('parses lines across arbitrary chunk boundaries', async () => {
    const chunks = [
      '{"a":1}\n{"b"',
      ':2}\n{"c":3}\n',
    ];
    const res = await parseNDJSON(streamFromStrings(chunks));
    expectEqual(res, [{a:1},{b:2},{c:3}]);
  });

  test('handles last line without trailing newline', async () => {
    const chunks = ['{"x":1}\n{"y":2}'];
    const res = await parseNDJSON(streamFromStrings(chunks));
    expectEqual(res, [{x:1},{y:2}]);
  });

  test('skips blank lines and trims CRLF', async () => {
    const chunks = ['\r\n', '{"n":1}\r\n', '\n', '{"m":2}\n'];
    const res = await parseNDJSON(streamFromStrings(chunks));
    expectEqual(res, [{n:1},{m:2}]);
  });

  test('reports malformed lines via onError and continues', async () => {
    const bad = []; // collect bad lines
    const chunks = ['{"ok":1}\n', '{bad json}\n', '{"ok":2}\n'];
    const res = await parseNDJSON(streamFromStrings(chunks), (line, err) => { bad.push(line); });
    expectEqual(res, [{ok:1},{ok:2}]);
    expectEqual(bad, ['{bad json}']);
  });
});

