const identity = (x) => x;
const square = (n) => n * n;

describe('Array.prototype.myMap', () => {
  test('identity', () => {
    expect([10].myMap(identity)).toEqual([10]);
    expect([10, 20].myMap(identity)).toEqual([10, 20]);
  });

  test('square', () => {
    expect([-4].myMap(square)).toEqual([16]);
    expect([5].myMap(square)).toEqual([25]);
  });

  test('skips holes and preserves length', () => {
    const a = [, 10, , 30];
    const r = a.myMap((v, i) => i);
    expect(r.length).toBe(4);
    expect(0 in r).toBe(false);
    expect(1 in r).toBe(true);
    expect(2 in r).toBe(false);
    expect(3 in r).toBe(true);
  });

  test('binds thisArg', () => {
    const ctx = { add: 5 };
    const out = [1].myMap(function (x) { return this.add + x; }, ctx);
    expect(out).toEqual([6]);
  });
});

