// @ts-nocheck
const _shimMap = (arr, cb, thisArg) => {
  const O = Object(arr);
  const len = O.length >>> 0;
  const A = new Array(len);
  for (let k = 0; k < len; k++) {
    if (k in O) A[k] = cb.call(thisArg, O[k], k, O);
  }
  return A;
};

const callMap = (arr, cb, thisArg) =>
  typeof arr.myMap === 'function' ? arr.myMap(cb, thisArg) : _shimMap(arr, cb, thisArg);

const identity = (element) => element;
const square = (element) => element * element;

describe('Array.prototype.myMap', () => {
  test('identity', () => {
    expect(callMap([10], identity)).toEqual([10]);
    expect(callMap([10, 20], identity)).toEqual([10, 20]);
  });

  test('square', () => {
    expect(callMap([-4], square)).toEqual([16]);
    expect(callMap([5], square)).toEqual([25]);
  });
});
