export default function chunk(arr, size){
  if(!Array.isArray(arr) || size <= 0) return [];
  const out = [];
  for(let i = 0; i <= arr.length; i += size){ // BUG: '<='
    out.push(arr.slice(i, i + size));
  }
  return out;
}

tests:
import chunk from './js-debug-off-by-one';

describe('chunk', () => {
  test('splits into uneven groups', () => {
    expect(chunk([1,2,3,4,5], 2)).toEqual([[1,2],[3,4],[5]]);
  });

  test('exactly divisible should not add empty chunk', () => {
    expect(chunk([1,2,3,4], 2)).toEqual([[1,2],[3,4]]);
  });

  test('invalid size or non-array returns []', () => {
    expect(chunk([1,2,3], 0)).toEqual([]);
    // @ts-ignore
    expect(chunk(null, 2)).toEqual([]);
  });
});

