export default function unique(arr){
  // BUG: sorts in-place and mutates input
  return Array.from(new Set(arr.sort()));
}

tests:
import unique from './js-debug-mutating-input';

describe('unique', () => {
  test('returns unique values', () => {
    expect(unique([2,2,1,3,1])).toEqual([2,1,3]);
  });

  test('does not mutate input', () => {
    const a = [2,2,1,3,1];
    unique(a);
    expect(a).toEqual([2,2,1,3,1]);
  });
});

