const cache = new Map();
const inflight = new Map();

export default async function fetchWithCache(key, fetcher){
  if(cache.has(key)) return cache.get(key);
  // BUG: not sharing in-flight promise
  const p = fetcher().then(res => { cache.set(key, res); inflight.delete(key); return res; });
  inflight.set(key, p);
  return p;
}

tests:
import fetchWithCache from './js-debug-async-race';

describe('fetchWithCache', () => {
  test('shares in-flight promise for concurrent calls', async () => {
    let calls = 0;
    const fetcher = async () => { calls++; await new Promise(r=>setTimeout(r,20)); return 'OK'; };
    const [a,b,c] = await Promise.all([
      fetchWithCache('k', fetcher),
      fetchWithCache('k', fetcher),
      fetchWithCache('k', fetcher)
    ]);
    expect(a).toBe('OK');
    expect(b).toBe('OK');
    expect(c).toBe('OK');
    expect(calls).toBe(1);
  });

  test('uses cached value on subsequent calls after resolve', async () => {
    let calls = 0;
    const fetcher = async () => { calls++; return 'VAL'; };
    await fetchWithCache('x', fetcher);
    const v2 = await fetchWithCache('x', fetcher);
    expect(v2).toBe('VAL');
    expect(calls).toBe(1);
  });
});

solution:
const cache = new Map();
const inflight = new Map();

export default async function fetchWithCache(key, fetcher){
  if(cache.has(key)) return cache.get(key);
  if(inflight.has(key)) return inflight.get(key);
  const p = fetcher().then(res => { cache.set(key, res); inflight.delete(key); return res; });
  inflight.set(key, p);
  return p;
}
