// Common order:
// microtask ‚Üí timer ‚Üí immediate

// Simple example
function makeCounter() {
  let count = 0; // stays in memory
  return function () {
    count++;
    return count;
  };
}

const counter = makeCounter();
console.log(counter()); // 1
console.log(counter()); // 2

// The inner function still 'remembers' count

// Example: once()
function once(fn) {
  let called = false;
  let value;
  return function (...args) {
    if (!called) {
      called = true;
      value = fn.apply(this, args);
    }
    return value;
  };
}

const init = once(() => console.log('Initialized'));
init(); // Logs once
init(); // Does nothing ‚Äî remembers state

const funcs = [];
for (var i = 0; i < 3; i++) {
  funcs.push(() => i);
}
console.log(funcs.map(fn => fn())); // [3, 3, 3]

// Fix with let
for (let j = 0; j < 3; j++) {
  setTimeout(() => console.log(j), 0);
}
// 0 1 2

// Example 1: Default binding
function show() {
  console.log(this);
}
show(); // window (or undefined in strict mode)

// Example 2: Method call
const user = {
  name: 'Mia',
  greet() {
    console.log('Hi, ' + this.name);
  }
};
user.greet(); // 'Hi, Mia'

// Example 3: Detached method loses context
const greetFn = user.greet;
greetFn(); // undefined or 'Hi, undefined' ‚Äî this is lost

function Timer() {
  this.seconds = 0;
  setInterval(() => {
    this.seconds++;
    console.log(this.seconds);
  }, 1000);
}
new Timer(); // works fine

// But using a normal function would break:
// setInterval(function() { this.seconds++ }, 1000); // this = undefined

