import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <main style="min-height:100vh; background:#0a0a0a; color:#e5e7eb; display:grid; place-items:center; padding:24px;">
      <section style="width:100%; max-width:420px;">
        <h1 style="margin:0 0 12px 0; font-size:20px; font-weight:700;">Simple Form</h1>

        <form (ngSubmit)="submit()" #f="ngForm" style="display:grid; gap:12px;">
          <label style="display:grid; gap:6px;">
            <span>Name</span>
            <input name="name" [(ngModel)]="model.name" />
          </label>

          <label style="display:grid; gap:6px;">
            <span>Email</span>
            <input name="email" [(ngModel)]="model.email" type="email" />
          </label>

          <button type="submit">Save</button>
        </form>

        <pre style="margin-top:12px; background:#0b1020; padding:8px; border-radius:8px;">{{ model | json }}</pre>
      </section>
    </main>
  `,
  styles: [
    `input { background:#111827; color:#e5e7eb; border:1px solid #1f2937; border-radius:8px; padding:10px; }`,
    `button { background:#a3e635; color:#111; border:none; border-radius:8px; padding:10px 14px; font-weight:600; cursor:pointer; }`
  ]
})
export class AppComponent {
  model = { name: '', email: '' };
  submit() {
    alert('Saved: ' + JSON.stringify(this.model));
    console.log(this.model);
  }
}

