import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  readonly minSize = 2;
  readonly maxSize = 20;

  size = 8;
  selectedKey: string | null = null;

  // TODO: Parse, clamp (2..20), apply new size, and clear previous selection.
  onSizeInput(raw: string): void {
    void raw;
  }

  // TODO: Save clicked cell key so only one cell is highlighted at a time.
  onCellClick(row: number, col: number): void {
    void row;
    void col;
    this.selectedKey = null;
  }

  get rows(): number[] {
    return Array.from({ length: this.size }, (_, index) => index);
  }

  get selectedLabel(): string {
    if (!this.selectedKey) return 'Selected: none';
    const [row, col] = this.selectedKey.split('-').map(Number);
    return 'Selected: row ' + String(row + 1) + ', column ' + String(col + 1);
  }

  cellKey(row: number, col: number): string {
    return String(row) + '-' + String(col);
  }

  cellClass(row: number, col: number): string {
    const dark = (row + col) % 2 === 1;
    const active = this.selectedKey === this.cellKey(row, col);
    return 'cell ' + (dark ? 'cell--dark' : 'cell--light') + (active ? ' cell--active' : '');
  }
}

