.page{min-height:100vh;display:grid;place-items:center;padding:24px;background:#0a0a0a;color:#e5e7eb;font-family:system-ui,-apple-system,Segoe UI,Roboto,Helvetica,Arial,sans-serif;}
.card{width:min(760px,100%);background:#0b1020;border:1px solid #1f2937;border-radius:14px;padding:18px;display:grid;gap:14px;}
.title{margin:0;font-size:20px;font-weight:800;}
.subtitle{margin:0;color:#9ca3af;font-size:13px;}
.size{display:inline-grid;gap:6px;font-size:12px;color:#9ca3af;width:max-content;}
.size input{width:90px;background:#0a0f1e;color:#e5e7eb;border:1px solid #374151;border-radius:10px;padding:8px 10px;}
.status{margin:0;padding:8px 10px;border:1px solid #1f2937;border-radius:10px;background:#0a0f1e;font-size:13px;}
.board{width:min(560px,100%);display:grid;border:1px solid #374151;border-radius:12px;overflow:hidden;}
.cell{aspect-ratio:1/1;border:none;cursor:pointer;outline:none;box-shadow:inset 0 0 0 1px rgba(15,23,42,.15);transition:box-shadow 120ms ease;}
.cell--light{background:#e5e7eb;}
.cell--dark{background:#334155;}
.cell:hover{box-shadow:inset 0 0 0 2px rgba(14,165,233,.65);}
.cell--active{box-shadow:inset 0 0 0 3px #facc15 !important;}
.cell:focus-visible{box-shadow:inset 0 0 0 3px #a3e635 !important;}

