import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

interface User {
  id: number;
  name: string;
  role: Role;
  active: boolean;
}

type Role = 'admin' | 'editor' | 'viewer';

type RoleFilter = 'all' | Role;

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  // TODO: Bind these to the controls in the template
  searchTerm = '';
  selectedRole: RoleFilter = 'all';
  showOnlyActive = false;

  // Fixed in-memory user list
  readonly users: User[] = [
    { id: 1, name: 'Alice Johnson', role: 'admin', active: true },
    { id: 2, name: 'Bob Smith', role: 'editor', active: false },
    { id: 3, name: 'Charlie Kim', role: 'viewer', active: true },
    { id: 4, name: 'Dana Patel', role: 'editor', active: true },
    { id: 5, name: 'Elliot Brown', role: 'viewer', active: false }
  ];

  // TODO: Return the list filtered by searchTerm, selectedRole, and showOnlyActive
  get filteredUsers(): User[] {
    return this.users;
  }

  trackByUserId(index: number, user: User): number {
    return user.id;
  }
}

