import { Component, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, ReactiveFormsModule, Validators } from '@angular/forms';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export default class AppComponent {
  readonly lastStepIndex = 2;
  currentStep = 0;

  private fb = inject(FormBuilder);

  // Top-level form with nested groups
  form: any = this.fb.group({
    basic: this.fb.group({
      name: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]]
    }),
    address: this.fb.group({
      street: ['', Validators.required],
      city: ['', Validators.required],
      country: ['', Validators.required],
      postalCode: ['']
    })
  });

  get basicGroup(): any {
    return this.form.get('basic') as any;
  }

  get addressGroup(): any {
    return this.form.get('address') as any;
  }

  get isFirstStep(): boolean {
    return this.currentStep === 0;
  }

  get isLastStep(): boolean {
    return this.currentStep === this.lastStepIndex;
  }

  get canGoNext(): boolean {
    if (this.currentStep === 0) {
      return this.basicGroup.valid;
    }
    if (this.currentStep === 1) {
      return this.addressGroup.valid;
    }
    return false;
  }

  private currentGroup(): any {
    if (this.currentStep === 0) {
      return this.basicGroup;
    }
    if (this.currentStep === 1) {
      return this.addressGroup;
    }
    return this.form;
  }

  next(): void {
    const group = this.currentGroup();
    group.markAllAsTouched();
    if (group.invalid) {
      return;
    }
    if (!this.isLastStep) {
      this.currentStep++;
    }
  }

  prev(): void {
    if (!this.isFirstStep) {
      this.currentStep--;
    }
  }

  submit(): void {
    this.form.markAllAsTouched();
    if (this.form.invalid) {
      return;
    }
    console.log('Signup submitted', this.form.value);
  }
}

export { AppComponent };

