import { Component, ViewChild, ElementRef } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

interface ChildNode {
  label: string;
  checked: boolean;
}

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  children: ChildNode[] = [
    { label: 'Child 1', checked: false },
    { label: 'Child 2', checked: false },
    { label: 'Child 3', checked: false }
  ];

  @ViewChild('parentBox', { static: true }) parentBox!: ElementRef<HTMLInputElement>;

  toggleParent(event: Event): void {
    const checked = (event.target as HTMLInputElement).checked;
    this.children = this.children.map(child => ({ ...child, checked }));
    this.updateParentState();
  }

  toggleChild(): void {
    this.updateParentState();
  }

  private updateParentState(): void {
    const total = this.children.length;
    const checkedCount = this.children.filter(c => c.checked).length;

    const parent = this.parentBox.nativeElement;

    if (checkedCount === total) {
      parent.checked = true;
      parent.indeterminate = false;
    } else if (checkedCount === 0) {
      parent.checked = false;
      parent.indeterminate = false;
    } else {
      parent.checked = false;
      parent.indeterminate = true;
    }
  }
}

