import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';

type Item = { id: string; label: string };

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  topItems: Item[] = [
    { id: 'a', label: 'Alpha' },
    { id: 'b', label: 'Beta' },
    { id: 'c', label: 'Gamma' },
    { id: 'd', label: 'Delta' },
    { id: 'e', label: 'Epsilon' }
  ];

  bottomItems: Item[] = [
    { id: 'x', label: 'Xenon' },
    { id: 'y', label: 'Yttrium' },
    { id: 'z', label: 'Zinc' }
  ];

  topSelected = new Set<string>();
  bottomSelected = new Set<string>();

  isTopSelected(id: string): boolean {
    return this.topSelected.has(id);
  }

  isBottomSelected(id: string): boolean {
    return this.bottomSelected.has(id);
  }

  toggleTop(id: string, checked: boolean): void {
    if (checked) this.topSelected.add(id);
    else this.topSelected.delete(id);
  }

  toggleBottom(id: string, checked: boolean): void {
    if (checked) this.bottomSelected.add(id);
    else this.bottomSelected.delete(id);
  }

  get canMoveDown(): boolean {
    return this.topSelected.size > 0;
  }

  get canMoveUp(): boolean {
    return this.bottomSelected.size > 0;
  }

  moveDown(): void {
    if (!this.canMoveDown) return;

    const moved: Item[] = [];
    const remaining: Item[] = [];

    for (const it of this.topItems) {
      if (this.topSelected.has(it.id)) moved.push(it);
      else remaining.push(it);
    }

    this.topItems = remaining;
    this.bottomItems = [...this.bottomItems, ...moved];
    this.topSelected.clear();
    this.activeCleanup();
  }

  moveUp(): void {
    if (!this.canMoveUp) return;

    const moved: Item[] = [];
    const remaining: Item[] = [];

    for (const it of this.bottomItems) {
      if (this.bottomSelected.has(it.id)) moved.push(it);
      else remaining.push(it);
    }

    this.bottomItems = remaining;
    this.topItems = [...this.topItems, ...moved];
    this.bottomSelected.clear();
    this.activeCleanup();
  }

  // Optional: if you ever add active row / keyboard nav later.
  private activeCleanup(): void {
    // no-op
  }

  trackById(_: number, item: Item): string {
    return item.id;
  }
}

