.page{min-height:100vh;display:grid;place-items:center;padding:24px;background:#0a0a0a;color:#e5e7eb;font-family:system-ui,-apple-system,Segoe UI,Roboto,Helvetica,Arial,sans-serif;}
.card{width:100%;max-width:780px;background:#0b1020;border:1px solid #1f2937;border-radius:14px;padding:18px;display:grid;gap:14px;}
.title{margin:0;font-size:18px;font-weight:800;}
.subtitle{margin:0;color:#9ca3af;font-size:12px;}
.layout{display:grid;grid-template-columns:1fr auto 1fr;gap:14px;align-items:start;}
.listCard{background:#0a0f1e;border:1px solid #1f2937;border-radius:12px;padding:12px;}
.listTitle{font-size:12px;color:#9ca3af;margin-bottom:8px;}
.list{list-style:none;margin:0;padding:0;display:grid;gap:6px;}
.row{padding:8px 10px;border:1px solid rgba(31,41,55,.5);border-radius:10px;}
.rowLabel{display:flex;gap:10px;align-items:center;cursor:pointer;}
.rowLabel input{accent-color:#a3e635;}
.controls{display:grid;gap:10px;place-items:center;padding-top:22px;}
.btn{width:44px;height:44px;border-radius:12px;border:1px solid #374151;background:#0a0f1e;color:#e5e7eb;font-weight:900;cursor:pointer;}
.btn:disabled{opacity:.5;cursor:not-allowed;}
.empty{padding:8px 10px;color:#9ca3af;}
.hint{margin:0;color:#9ca3af;font-size:12px;}
@media (max-width:720px){.layout{grid-template-columns:1fr;}.controls{grid-template-columns:1fr 1fr;justify-content:center;padding-top:0;}}
