import React, { useState } from 'react';
import './App.css';

export default function App() {
  // Form field state (TODO: wire these up with validation)
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  // Touched flags for showing validation messages
  const [touchedName, setTouchedName] = useState(false);
  const [touchedEmail, setTouchedEmail] = useState(false);
  const [touchedMessage, setTouchedMessage] = useState(false);

  // Submission state + feedback messages
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  // TODO: Replace this with a real validity check
  // (all fields required + basic email + min length for message)
  const formIsValid = false;

  const handleSubmit = (event: any) => {
    event.preventDefault();

    // TODO: Implement the full submit logic
    // - Mark all fields as touched
    // - Validate values and bail out if invalid
    // - If valid:
    //   - Clear previous success/error messages
    //   - Set isSubmitting = true
    //   - Send a POST request with { name, email, message } to
    //     https://jsonplaceholder.typicode.com/posts
    //   - On success, show "Your message has been sent. Thank you!" and
    //     optionally reset the form
    //   - On error, show "Something went wrong. Please try again."

    console.log('submit() called with:', { name, email, message });
  };

  return (
    <main className="main">
      <section className="section">
        <h1>Contact Us</h1>

        <form className="panel" noValidate onSubmit={handleSubmit}>
          <div className="field">
            <label htmlFor="name">Name</label>
            <input
              id="name"
              type="text"
              value={name}
              onChange={(event) => setName(event.target.value)}
              onBlur={() => setTouchedName(true)}
            />
            {/* TODO: Show a required error message when name is empty and touched */}
          </div>

          <div className="field">
            <label htmlFor="email">Email</label>
            <input
              id="email"
              type="email"
              value={email}
              onChange={(event) => setEmail(event.target.value)}
              onBlur={() => setTouchedEmail(true)}
            />
            {/* TODO: Show required + invalid email messages */}
          </div>

          <div className="field">
            <label htmlFor="message">Message</label>
            <textarea
              id="message"
              rows={4}
              value={message}
              onChange={(event) => setMessage(event.target.value)}
              onBlur={() => setTouchedMessage(true)}
            />
            {/* TODO: Show a required / minimum-length error message */}
          </div>

          <button
            type="submit"
            className="primary"
            disabled={!formIsValid || isSubmitting}
          >
            {isSubmitting ? 'Sending…' : 'Send message'}
          </button>

          {successMessage && <p className="success">{successMessage}</p>}
          {errorMessage && <p className="error">{errorMessage}</p>}
        </form>
      </section>
    </main>
  );
}

files.src/App.css:
:root { color-scheme: dark; }
html, body { margin: 0; height: 100%; background:#020617; font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif; }

.main { min-height:100vh; background:#020617; color:#e5e7eb; display:grid; place-items:center; padding:24px; }
.section { width:100%; max-width:480px; display:grid; gap:16px; }
h1 { margin:0; font-size:20px; font-weight:700; }

.panel { background:#020617; border:1px solid #1f2937; border-radius:12px; padding:16px; display:grid; gap:12px; }
.field { display:grid; gap:4px; }
label { font-size:13px; color:#9ca3af; }
input, textarea { background:#020617; border-radius:8px; border:1px solid #1f2937; padding:8px 10px; color:#e5e7eb; font-size:14px; }
input:focus, textarea:focus { outline:none; border-color:#6366f1; box-shadow:0 0 0 1px #6366f1; }
button.primary { margin-top:4px; background:#4ade80; color:#022c22; border:none; border-radius:999px; padding:10px 16px; font-weight:600; font-size:14px; cursor:pointer; display:inline-flex; align-items:center; justify-content:center; }
button.primary[disabled] { opacity:.6; cursor:not-allowed; }
.success { margin:4px 0 0; font-size:13px; color:#22c55e; }
.error { margin:4px 0 0; font-size:13px; color:#f97316; }
