import React, { useState, useEffect } from 'react';
import './App.css';

interface Result {
  id: number;
  name: string;
  email: string;
}

const FAKE_DATA: Result[] = [
  { id: 1, name: 'Alice Johnson', email: 'alice@example.com' },
  { id: 2, name: 'Bob Smith', email: 'bob@example.com' },
  { id: 3, name: 'Charlie Kim', email: 'charlie@example.com' },
  { id: 4, name: 'Diana Lopez', email: 'diana@example.com' },
  { id: 5, name: 'Ethan Brown', email: 'ethan@example.com' },
  { id: 6, name: 'Fiona Patel', email: 'fiona@example.com' }
];

// Fake API: filters the in-memory array and responds after a delay.
// Treat this as given and focus on wiring up the debounced React logic.
function fakeApiSearch(term: string): Promise<Result[]> {
  const trimmed = term.trim().toLowerCase();
  const delayMs = 600;

  return new Promise((resolve, reject) => {
    window.setTimeout(() => {
      if (!trimmed) {
        resolve([]);
        return;
      }

      if (trimmed === 'error') {
        reject(new Error('Fake API error'));
        return;
      }

      const filtered = FAKE_DATA.filter((item) => {
        const name = item.name.toLowerCase();
        const email = item.email.toLowerCase();
        return name.includes(trimmed) || email.includes(trimmed);
      });

      resolve(filtered);
    }, delayMs);
  });
}

