  // }, [query]);

  const handleChange = (event) => {
    const value = event.target.value;
    setQuery(value);
    // Optional: you may want to clear error here when the user types again.
  };

  return (
    <main className="main">
      <section className="card">
        <header className="header">
          <h1 className="title">Debounced Search</h1>
          <p className="subtitle">Type to search a fake user directory. Search runs 500ms after you stop typing.</p>
        </header>

        <div className="field">
          <label className="label" htmlFor="search-input">Search</label>
          <input
            id="search-input"
            className="input"
            type="text"
            value={query}
            onChange={handleChange}
            placeholder="Start typing a name or email..."
            autoComplete="off"
          />
        </div>

        <div className="status-row">
