import React, { useState, ChangeEvent } from 'react';
import './App.css';

export default function App() {
  const [rowInput, setRowInput] = useState(3);
  const [colInput, setColInput] = useState(4);

  const [rows, setRows] = useState([]);
  const [cols, setCols] = useState([]);

  const normalizeCount = (raw: number): number => {
    return 0; // TODO
  };

  const updateTable = () => {
    // TODO
  };

  const handleRowChange = (e) => {
    setRowInput(Number(e.target.value));
  };

  const handleColChange = (e) => {
    setColInput(Number(e.target.value));
  };

  const hasGrid = rows.length > 0 && cols.length > 0;

  return (
    <main className="main">
      <section className="section">
        <h1>Dynamic Table</h1>
        <p className="hint">Enter rows & columns, then generate a table grid.</p>

        <div className="controls">
          <label>
            <span>Rows</span>
            <input type="number" min={0} value={rowInput} onChange={handleRowChange} />
          </label>

          <label>
            <span>Columns</span>
            <input type="number" min={0} value={colInput} onChange={handleColChange} />
          </label>

          <button onClick={updateTable}>Generate table</button>
        </div>

        {hasGrid ? (
          <div className="preview" aria-label="dynamic table">
            <table className="grid">
              <tbody>
                {rows.map((_, rowIndex) => (
                  <tr key={rowIndex}>
                    {cols.map((__, colIndex) => (
                      <td key={colIndex}>R{rowIndex + 1}C{colIndex + 1}</td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <p className="empty">Set rows & columns, then generate the table.</p>
        )}
      </section>
    </main>
  );
}

files.src/App.css:
:root { color-scheme: dark; }
html, body { margin: 0; height: 100%; background:#020617; font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif; }

.main { min-height:100vh; background:#020617; color:#e5e7eb; display:grid; place-items:center; padding:24px; }
.section { width:100%; max-width:640px; display:grid; gap:16px; }
h1 { margin:0; font-size:20px; font-weight:700; }
.hint { margin:0; font-size:13px; color:#9ca3af; }

.controls { display:flex; flex-wrap:wrap; gap:12px; align-items:flex-end; margin-top:8px; }
.controls label { display:flex; flex-direction:column; gap:4px; font-size:13px; }
.controls input { background:#020617; border:1px solid #374151; border-radius:10px; padding:6px 10px; color:#e5e7eb; min-width:80px; }
.controls button { background:#a3e635; color:#0b1020; border:none; border-radius:10px; padding:8px 14px; font-weight:600; cursor:pointer; }
.controls button:hover { filter:brightness(1.05); }

.preview { overflow:auto; border-radius:12px; border:1px solid #1f2937; padding:12px; background:#020617; margin-top:8px; }
.grid { width:100%; border-collapse:collapse; }
.grid td { border:1px solid #1f2937; padding:6px 10px; font-size:13px; text-align:center; }

.empty { margin:8px 0 0; font-size:13px; color:#9ca3af; }
