import React, { useState } from 'react';
import './App.css';

export default function App() {
  // 0 = basic, 1 = address, 2 = summary
  const [currentStep, setCurrentStep] = useState(0);

  // Top-level form state (controlled inputs)
  const [form, setForm] = useState({
    basic: { name: '', email: '' },
    address: { street: '', city: '', country: '', postalCode: '' }
  });

  // Placeholder error + validation flags (candidate will implement real validation)
  const [showErrors] = useState(false);
  const basicErrors = { name: false, email: false };
  const addressErrors = { street: false, city: false, country: false };

  const isFirstStep = currentStep === 0;
  const isLastStep = currentStep === 2;

  // TODO: Derive this from validation per step
  const canGoNext = false;

  function updateBasic(key: 'name' | 'email', value: string) {
    setForm(prev => ({
      ...prev,
      basic: { ...prev.basic, [key]: value }
    }));
  }

  function updateAddress(key: 'street' | 'city' | 'country' | 'postalCode', value: string) {
    setForm(prev => ({
      ...prev,
      address: { ...prev.address, [key]: value }
    }));
  }

  function next() {
    // TODO: block if current step is invalid
  }

  function prev() {
    // TODO: go back one step if not on the first step
  }

  function submit() {
    // TODO: validate all steps and submit aggregated data
    console.log('submit() called with value:', form);
  }

  const { basic, address } = form;

  return (
    <main className="app">
      <section className="card">
        <header className="header">
          <h1>Multi-step Signup</h1>
          <p className="subtitle">Step {currentStep + 1} of 3</p>
        </header>

        <section className="steps">
          {currentStep === 0 && (
            <div className="step">
              <h2 className="step-title">Basic info</h2>

              <label className="field">
                <span>Name</span>
                <input
                  type="text"
                  placeholder="Jane Doe"
                  value={basic.name}
                  onChange={e => updateBasic('name', e.target.value)}
                />
                {showErrors && basicErrors.name && (
                  <small className="error">Name is required.</small>
                )}
              </label>

              <label className="field">
                <span>Email</span>
                <input
                  type="email"
                  placeholder="jane@example.com"
                  value={basic.email}
                  onChange={e => updateBasic('email', e.target.value)}
                />
                {showErrors && basicErrors.email && (
                  <small className="error">Please enter a valid email.</small>
                )}
              </label>
            </div>
          )}

          {currentStep === 1 && (
            <div className="step">
              <h2 className="step-title">Address</h2>

              <label className="field">
                <span>Street</span>
                <input
                  type="text"
                  placeholder="123 Main St"
                  value={address.street}
                  onChange={e => updateAddress('street', e.target.value)}
                />
                {showErrors && addressErrors.street && (
                  <small className="error">Street is required.</small>
                )}
              </label>

              <label className="field">
                <span>City</span>
                <input
                  type="text"
                  placeholder="Istanbul"
                  value={address.city}
                  onChange={e => updateAddress('city', e.target.value)}
                />
                {showErrors && addressErrors.city && (
                  <small className="error">City is required.</small>
                )}
              </label>

              <label className="field">
                <span>Country</span>
                <input
                  type="text"
                  placeholder="Türkiye"
                  value={address.country}
                  onChange={e => updateAddress('country', e.target.value)}
                />
                {showErrors && addressErrors.country && (
                  <small className="error">Country is required.</small>
                )}
              </label>

              <label className="field">
                <span>Postal code (optional)</span>
                <input
                  type="text"
                  placeholder="34000"
                  value={address.postalCode}
                  onChange={e => updateAddress('postalCode', e.target.value)}
                />
              </label>
            </div>
          )}

          {currentStep === 2 && (
            <div className="step">
              <h2 className="step-title">Summary</h2>
              <div className="summary">
                <h3>Basic info</h3>
                <p><strong>Name:</strong> {basic.name || '—'}</p>
                <p><strong>Email:</strong> {basic.email || '—'}</p>

                <h3>Address</h3>
                <p><strong>Street:</strong> {address.street || '—'}</p>
                <p><strong>City:</strong> {address.city || '—'}</p>
                <p><strong>Country:</strong> {address.country || '—'}</p>
                <p><strong>Postal code:</strong> {address.postalCode || '—'}</p>
              </div>
            </div>
          )}
        </section>

        <footer className="actions">
          <button
            type="button"
            className="ghost"
            onClick={prev}
            disabled={isFirstStep}
          >
            Back
          </button>

          {!isLastStep && (
            <button
              type="button"
              onClick={next}
              disabled={!canGoNext}
            >
              Next
            </button>
          )}

          {isLastStep && (
            <button
              type="button"
              className="primary"
              onClick={submit}
            >
              Submit
            </button>
          )}
        </footer>
      </section>
    </main>
  );
}

files.src/App.css:
:root { color-scheme: dark; }
html, body { margin: 0; height: 100%; background:#020617; font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif; }

.app { min-height:100vh; display:grid; place-items:center; background:#020617; color:#e5e7eb; padding:24px; }
.card { width:100%; max-width:520px; background:#020617; border-radius:16px; border:1px solid #1f2937; padding:20px 20px 16px; display:grid; gap:16px; }
.header h1 { margin:0; font-size:20px; font-weight:700; }
.subtitle { margin:4px 0 0; font-size:13px; color:#9ca3af; }
.steps { min-height:260px; }
.step { display:grid; gap:12px; }
.step-title { margin:0 0 4px; font-size:16px; font-weight:600; }
.field { display:grid; gap:4px; font-size:13px; }
.field span { color:#e5e7eb; }
.field input { border-radius:10px; border:1px solid #374151; background:#020617; padding:8px 10px; color:#e5e7eb; font-size:14px; }
.field input:focus { outline:none; border-color:#a3e635; }
.error { font-size:11px; color:#f97316; }
.summary { display:grid; gap:4px; font-size:14px; }
.summary h3 { margin:8px 0 2px; font-size:14px; font-weight:600; color:#e5e7eb; }
.actions { display:flex; justify-content:space-between; gap:8px; margin-top:4px; }
.actions button { border-radius:999px; border:1px solid #374151; background:#020617; color:#e5e7eb; padding:8px 16px; font-size:14px; font-weight:600; cursor:pointer; }
.actions button.primary { background:#a3e635; color:#020617; border-color:#a3e635; }
.actions button[disabled] { opacity:.5; cursor:not-allowed; }
.actions button.ghost { background:transparent; }
