:root { color-scheme: dark; }
html, body { margin: 0; height: 100%; background:#0a0a0a; font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif; }

.main { min-height:100vh; background:#0a0a0a; color:#e5e7eb; display:grid; place-items:center; padding:24px; }
.section { width:100%; max-width:420px; display:grid; gap:16px; }
h1 { margin:0; font-size:20px; font-weight:700; }

.panel { background:#0b1020; border:1px solid #1f2937; border-radius:12px; padding:16px; display:grid; gap:12px; }
.parent { display:flex; align-items:center; gap:8px; font-size:16px; font-weight:600; }
.children { display:grid; gap:6px; margin-left:24px; }
.child { display:flex; align-items:center; gap:8px; font-size:14px; }
.child input[type=checkbox], .parent input[type=checkbox] { width:16px; height:16px; }
