  // TODO: find node by id in a tree
  const findById = (_nodes: CommentNode[], _id: string): CommentNode | null => {
    return null;
  };

  const Tree = useMemo(() => {
    const Render = ({ nodes, depth }: { nodes: CommentNode[]; depth: number }) => {
      return (
        <>
          {nodes.map((c) => (
            <div key={c.id} className="node" style={{ marginLeft: depth * 16 }}>
              <div className="bubble">
                <div className="text">{c.text}</div>
                <div className="meta">
                  <button className="ghost" onClick={() => openReply(c.id)}>Reply</button>
                </div>

                {/* Reply box: should open ONLY for the active comment */}
                {activeReplyId === c.id && (
                  <div className="reply">
                    <input
                      value={replyDraft}
                      onChange={(e) => setReplyDraft(e.target.value)}
                      placeholder="Write a reply..."
                    />
                    <div className="replyActions">
                      <button onClick={() => submitReply(c.id)}>Post</button>
                      <button className="ghost" onClick={cancelReply}>Cancel</button>
                    </div>
                  </div>
                )}
              </div>

              {c.children.length > 0 && <Render nodes={c.children} depth={depth + 1} />}
