        <div className="hint">Goal: replying should not open inputs on other comments. Replies must nest infinitely with indentation.</div>
      </section>
    </main>
  );
}

files.src/App.css:
:root { color-scheme: dark; }
html, body { margin: 0; height: 100%; background:#0a0a0a; font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif; }

.page { min-height:100vh; display:grid; place-items:center; background:#0a0a0a; padding:24px; }
.card { width:100%; max-width:720px; background:#0b1020; border:1px solid #1f2937; border-radius:16px; padding:18px; display:grid; gap:14px; color:#e5e7eb; }
.title { margin:0; font-size:20px; font-weight:900; color:#e5e7eb; }
.subtitle { margin:0; color:#9ca3af; font-size:12px; line-height:1.45; }

.composer { display:grid; gap:10px; }
textarea { width:100%; resize:vertical; min-height:70px; background:#0a0f1e; color:#e5e7eb; border:1px solid #1f2937; border-radius:12px; padding:10px; }
textarea::placeholder { color:#6b7280; }

.list { display:grid; gap:10px; }
.node { display:grid; gap:8px; }
.bubble { background:#0a0f1e; border:1px solid #1f2937; border-radius:14px; padding:12px; display:grid; gap:10px; color:#e5e7eb; }
.text { font-size:14px; line-height:1.4; color:#e5e7eb; }
.meta { display:flex; gap:8px; }

.reply { display:grid; gap:8px; margin-top:6px; }
.reply input { width:100%; background:#0b1020; color:#e5e7eb; border:1px solid #1f2937; border-radius:12px; padding:10px; }
.reply input::placeholder { color:#6b7280; }
.replyActions { display:flex; gap:8px; flex-wrap:wrap; }

button { background:#a3e635; color:#111; border:none; border-radius:12px; padding:10px 14px; font-weight:900; cursor:pointer; }
button.ghost { background:transparent; color:#e5e7eb; border:1px solid #374151; }

.hint { color:#9ca3af; font-size:12px; line-height:1.45; }
