  { id: 'p1', name: 'Wireless Headphones', price: 129.99 },
  { id: 'p2', name: 'Mechanical Keyboard', price: 89.5 },
  { id: 'p3', name: 'Ergonomic Mouse', price: 49.99 },
  { id: 'p4', name: 'USB-C Hub', price: 39.0 }
];

