        </section>

        {/* Cart */}
        <section className="column">
          <header className="cart-header">
            <h1 className="title">Cart</h1>
            <p className="subtitle">
              Total items: <strong>{totalQty}</strong> · Total: <strong>${totalPrice.toFixed(2)}</strong>
