import React, { useMemo, useState } from 'react';
import './App.css';

type Item = { id: string; label: string };

const INITIAL_TOP: Item[] = [
  { id: 'a', label: 'Alpha' },
  { id: 'b', label: 'Beta' },
  { id: 'c', label: 'Gamma' },
  { id: 'd', label: 'Delta' },
  { id: 'e', label: 'Epsilon' }
];

const INITIAL_BOTTOM: Item[] = [
  { id: 'x', label: 'Xenon' },
  { id: 'y', label: 'Yttrium' },
  { id: 'z', label: 'Zinc' }
];

export default function App() {
  const [topItems, setTopItems] = useState<Item[]>(INITIAL_TOP);
  const [bottomItems, setBottomItems] = useState<Item[]>(INITIAL_BOTTOM);

  // Selected ids per list
  const [topSelected, setTopSelected] = useState<Set<string>>(new Set<string>());
  const [bottomSelected, setBottomSelected] = useState<Set<string>>(new Set<string>());

  // TODO: compute button enablement
  const canMoveDown = false;
  const canMoveUp = false;

  // TODO: toggle selection (must create a NEW Set)
  const toggleTop = (id: string, checked: boolean) => {};
  const toggleBottom = (id: string, checked: boolean) => {};

  // TODO: move selected Top -> Bottom (append), clear topSelected
  const moveDown = () => {};

  // TODO: move selected Bottom -> Top (append), clear bottomSelected
  const moveUp = () => {};

  return (
    <main className="page">
      <section className="card">
        <h1 className="title">Transfer List</h1>
        <p className="subtitle">Select items and move them between lists.</p>

        <div className="layout">
          <section className="listCard">
            <div className="listTitle">Top</div>
            <ul className="list" role="list">
              {topItems.map((item) => (
                <li key={item.id} className="row">
                  <label className="rowLabel">
                    <input
                      type="checkbox"
                      checked={topSelected.has(item.id)}
                      onChange={(e) => toggleTop(item.id, e.target.checked)}
                    />
                    <span>{item.label}</span>
                  </label>
                </li>
              ))}
              {topItems.length === 0 && <li className="empty">Empty</li>}
            </ul>
          </section>

          <section className="controls">
            <button className="btn" onClick={moveDown} disabled={!canMoveDown} aria-label="move down">
              ↓
            </button>
            <button className="btn" onClick={moveUp} disabled={!canMoveUp} aria-label="move up">
              ↑
            </button>
          </section>

          <section className="listCard">
            <div className="listTitle">Bottom</div>
            <ul className="list" role="list">
              {bottomItems.map((item) => (
                <li key={item.id} className="row">
                  <label className="rowLabel">
                    <input
                      type="checkbox"
                      checked={bottomSelected.has(item.id)}
                      onChange={(e) => toggleBottom(item.id, e.target.checked)}
                    />
                    <span>{item.label}</span>
                  </label>
                </li>
              ))}
              {bottomItems.length === 0 && <li className="empty">Empty</li>}
            </ul>
          </section>
        </div>

        <p className="hint">Tip: selections are per-list. Move clears selection of moved items.</p>
      </section>
    </main>
  );
}

