import React, { useState } from 'react';
import './App.css';

type FaqItem = {
  question: string;
  answer: string;
};

type FaqItemProps = {
  question: string;
  answer: string;
  isOpen: boolean;
  onToggle: () => void;
  key?: string | number;
};

function FaqItem({ question, answer, isOpen, onToggle }: FaqItemProps) {
  const handleHeaderClick = () => {
    onToggle();
  };

  return (
    <article className={"faq-item" + (isOpen ? " open" : "")}>
      <button
        type="button"
        className="faq-header"
        onClick={handleHeaderClick}
        aria-expanded={isOpen}
      >
        <span className="question">{question}</span>
        <span className="indicator">{isOpen ? '-' : '+'}</span>
      </button>

      {isOpen && (
        <div className="faq-body">
          <p>{answer}</p>
        </div>
      )}
    </article>
  );
}

const faqItems: FaqItem[] = [
  {
    question: 'Lorem ipsum dolor sit amet?',
    answer: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed non risus euismod, posuere justo ut, pulvinar neque.'
  },
  {
    question: 'Curabitur blandit tempus porttitor?',
    answer: 'Curabitur blandit tempus porttitor. Integer posuere erat a ante venenatis dapibus posuere velit aliquet.'
  },
  {
    question: 'Aenean lacinia bibendum nulla sed consectetur?',
    answer: 'Aenean lacinia bibendum nulla sed consectetur. Cras justo odio, dapibus ac facilisis in, egestas eget quam.'
  },
  {
    question: 'Vivamus sagittis lacus vel augue laoreet rutrum?',
    answer: 'Vivamus sagittis lacus vel augue laoreet rutrum faucibus dolor auctor. Maecenas faucibus mollis interdum.'
  }
];

export default function App() {
  const [allowMultiple, setAllowMultiple] = useState(false);
  const [openIndexes, setOpenIndexes] = useState([]);

  const isItemOpen = (index: number): boolean => {
    return openIndexes.includes(index);
  };

  const onItemToggle = (index: number) => {
    const currentlyOpen = isItemOpen(index);

    if (allowMultiple) {
      // Multi-open: treat openIndexes as a set
      setOpenIndexes(prev =>
        currentlyOpen ? prev.filter(i => i !== index) : [...prev, index]
      );
      return;
    }

    // Single-open mode: only one index at a time
    if (currentlyOpen) {
      setOpenIndexes([]);
    } else {
      setOpenIndexes([index]);
    }
  };

  const onToggleAllowMultiple = () => {
    setAllowMultiple(prev => {
      const next = !prev;

      if (!next && openIndexes.length > 1) {
        // Normalize when going back to single-open: keep only the first open index
        setOpenIndexes(current => (current.length ? [current[0]] : []));
      }

      return next;
    });
  };

  return (
    <main className="app-root">
      <section className="card">
        <header className="header">
          <h1>FAQ (Solution)</h1>
          <label className="mode-toggle">
            <input
              type="checkbox"
              checked={allowMultiple}
              onChange={onToggleAllowMultiple}
            />
            <span>Allow multiple open</span>
          </label>
        </header>

        <div className="faq-list">
          {faqItems.map((item, index) => (
            <FaqItem
              key={item.question}
              question={item.question}
              answer={item.answer}
              isOpen={isItemOpen(index)}
              onToggle={() => onItemToggle(index)}
            />
          ))}
        </div>
      </section>
    </main>
  );
}

