:root { color-scheme: dark; }
html, body { margin: 0; height: 100%; background:#020617; font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif; }

.app-root { min-height:100vh; background:#020617; color:#e5e7eb; display:grid; place-items:center; padding:24px; }
.card { width:100%; max-width:520px; background:#020617; border-radius:16px; border:1px solid #1f2937; padding:20px; box-sizing:border-box; }
.header { display:flex; align-items:center; justify-content:space-between; margin-bottom:12px; }
.header h1 { margin:0; font-size:20px; font-weight:700; }
.mode-toggle { display:flex; align-items:center; gap:6px; font-size:12px; color:#9ca3af; }
.faq-list { margin-top:4px; }

.faq-item { background:#0b1020; border-radius:12px; border:1px solid #1f2937; overflow:hidden; transition:background-color .15s ease, border-color .15s ease; }
.faq-item + .faq-item { margin-top:8px; }
.faq-item.open { background:#020617; border-color:#4b5563; }

.faq-header { width:100%; display:flex; justify-content:space-between; align-items:center; gap:8px; padding:12px 14px; background:transparent; border:none; color:#e5e7eb; font-size:14px; cursor:pointer; text-align:left; }
.faq-header:hover { background:rgba(148,163,184,0.06); }
.indicator { font-weight:700; }
.faq-body { padding:0 14px 12px; font-size:13px; color:#9ca3af; }
