import React, { useState } from 'react';
import './App.css';

export default function App() {
  const [counts, setCounts] = useState<number[]>([0]);

  const onClick = (index: number) => {
    setCounts((prev) => {
      const next = [...prev];
      next[index] += 1;
      next.push(0);
      return next;
    });
  };

  return (
    <main className="page">
      <section className="card">
        <h1 className="title">Dynamic Counter Buttons (Solution)</h1>
        <p className="subtitle">Each click increments only the clicked button and appends a new 0 button.</p>

        <div className="list">
          {counts.map((c, i) => (
            <button
              key={i}
              className="counterBtn"
              onClick={() => onClick(i)}
              type="button"
            >
              {c}
            </button>
          ))}
        </div>

        <div className="hint">Try clicking different buttons: each one keeps its own count.</div>
      </section>
    </main>
  );
}

