import React, { useState } from 'react';
import './App.css';

type Slide = {
  id: number;
  title: string;
  imageUrl: string;
};

const SLIDES: Slide[] = [
  {
    id: 1,
    title: 'Snowflakes glowing under city lights',
    imageUrl: 'https://picsum.photos/seed/slide-1/800/400'
  },
  {
    id: 2,
    title: 'Forest trail in the morning',
    imageUrl: 'https://images.pexels.com/photos/1563604/pexels-photo-1563604.jpeg'
  },
  {
    id: 3,
    title: 'City lights at night',
    imageUrl: 'https://images.pexels.com/photos/220067/pexels-photo-220067.jpeg'
  }
];

export default function App() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const totalSlides = SLIDES.length;
  const currentSlide = SLIDES[currentIndex];

  const canGoPrev = currentIndex > 0;
  const canGoNext = currentIndex < totalSlides - 1;

  const goTo = (index: number) => {
    if (index < 0 || index >= totalSlides) return;
    setCurrentIndex(index);
  };

  const prev = () => {
    if (!canGoPrev) return;
    setCurrentIndex((i) => i - 1);
  };

  const next = () => {
    if (!canGoNext) return;
    setCurrentIndex((i) => i + 1);
  };

  return (
    <main className="main">
      <section className="section">
        <h1>Image Slider (Solution)</h1>

        <figure className="frame">
          <img
            className="image"
            src={currentSlide.imageUrl}
            alt={currentSlide.title}
          />
          <figcaption className="caption">{currentSlide.title}</figcaption>
        </figure>

        <div className="meta">
          Slide {currentIndex + 1} of {totalSlides}
        </div>

        <div className="actions">
          <button type="button" onClick={prev} disabled={!canGoPrev}>
            Previous
          </button>
          <button type="button" onClick={next} disabled={!canGoNext}>
            Next
          </button>
        </div>

        <div className="dots">
          {SLIDES.map((slide, index) => (
            <button
              key={slide.id}
              type="button"
              className={
                'dot' + (index === currentIndex ? ' dot--active' : '')
              }
              onClick={() => goTo(index)}
              aria-label={`Go to slide ${index + 1}`}
            />
          ))}
        </div>
      </section>
    </main>
  );
}

files.src/App.css:
:root { color-scheme: dark; }
html, body { margin: 0; height: 100%; background:#0a0a0a; font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif; }

.main { min-height:100vh; background:#0a0a0a; color:#e5e7eb; display:grid; place-items:center; padding:24px; }
.section { width:100%; max-width:520px; display:grid; gap:16px; }
h1 { margin:0; font-size:20px; font-weight:700; }

.frame { margin:0; border-radius:12px; overflow:hidden; border:1px solid #1f2937; background:#020617; }
.image { display:block; width:100%; height:240px; object-fit:cover; }
.caption { padding:8px 12px; font-size:14px; color:#e5e7eb; background:rgba(15,23,42,.9); }
.meta { font-size:12px; color:#9ca3af; text-align:center; }
.actions { display:flex; gap:8px; justify-content:center; margin-top:4px; }

button { background:#a3e635; color:#111; border:none; border-radius:10px; padding:10px 14px; font-weight:700; cursor:pointer; }
button[disabled] { opacity:.5; cursor:not-allowed; }
.dots { display:flex; justify-content:center; gap:6px; margin-top:6px; }
.dot { width:8px; height:8px; border-radius:999px; border:none; background:#374151; cursor:pointer; padding:0; }
.dot.dot--active { background:#a3e635; }
