import React, { useMemo, useState } from 'react';
import './App.css';

export default function App() {
  const [progress, setProgress] = useState(0);

  const barClass = useMemo(() => {
    if (progress < 34) return 'bar red';
    if (progress <= 66) return 'bar orange';
    return 'bar green';
  }, [progress]);

  const inc = () => {
    setProgress((p) => Math.min(100, p + 10));
  };

  const dec = () => {
    setProgress((p) => Math.max(0, p - 10));
  };

  return (
    <main className="page">
      <section className="card">
        <h1 className="title">Progress Bar (Solution)</h1>
        <p className="subtitle">+10% / −10% with clamping and threshold-based colors.</p>

        <div className="value">{progress}%</div>

        <div
          className="track"
          role="progressbar"
          aria-valuemin={0}
          aria-valuemax={100}
          aria-valuenow={progress}
        >
          <div className={barClass} style={{ width: `${progress}%` }} />
        </div>

        <div className="actions">
          <button onClick={dec} className="ghost" aria-label="decrease">−10%</button>
          <button onClick={inc} aria-label="increase">+10%</button>
        </div>

        <div className="hint">Color thresholds: &lt;34 red, 34–66 orange, &gt;66 green.</div>
      </section>
    </main>
  );
}
