import { useMemo, useState } from 'react';
import './App.css';

type StarRatingProps = {
  max?: number;
  rating: number;
  onChange: (value: number) => void;
};

function StarRating({ max = 5, rating, onChange }: StarRatingProps) {
  const stars = useMemo(
    () => Array.from({ length: max }, (_, i) => i + 1),
    [max]
  );

  const handleRate = (value: number) => {
    if (value < 1 || value > max) return;
    onChange(value);
  };

  return (
    <div className="stars" role="radiogroup" aria-label="Star rating">
      {stars.map((star) => (
        <button
          key={star}
          type="button"
          className={"star" + (star <= rating ? " filled" : "")}
          onClick={() => handleRate(star)}
          aria-label={`${star} out of ${max}`}
          aria-pressed={star <= rating}
        >
          ★
        </button>
      ))}
    </div>
  );
}

export default function App() {
  const [currentRating, setCurrentRating] = useState(0);

  return (
    <main className="main">
      <section className="section">
        <h1>Star Rating Widget (Solution)</h1>

        <div className="panel">
          <StarRating rating={currentRating} onChange={setCurrentRating} />
          <p className="hint">Current rating: {currentRating} / 5</p>
        </div>
      </section>
    </main>
  );
}

