import App from './App.vue';
import './App.css';

createApp(App).mount('#app');

/src/App.css.code:
:root {
  color-scheme: light;
  --bg: #f9fafb;
  --fg: #020617;
  --card-bg: #ffffff;
  --border: #e5e7eb;
  --accent: #4f46e5;
  --accent-soft: #eef2ff;
}

:root[data-theme='dark'] {
  color-scheme: dark;
  --bg: #020617;
  --fg: #e5e7eb;
  --card-bg: #020617;
  --border: #1f2937;
  --accent: #a855f7;
  --accent-soft: #111827;
}

html, body {
  margin: 0;
  height: 100%;
}

body {
  background: var(--bg);
  color: var(--fg);
  font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif;
}

.app-shell {
  min-height: 100vh;
  background: #f9fafb;
  color: #020617;
  display: flex;
  flex-direction: column;
  padding: 16px;
  box-sizing: border-box;
}

.app-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 24px;
}

.app-title {
  margin: 0;
  font-size: 18px;
  font-weight: 700;
}

.app-content {
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: flex-start;
}

.preview-card {
  width: 100%;
  max-width: 480px;
  background: #ffffff;
  border-radius: 12px;
  border: 1px solid #e5e7eb;
  padding: 20px;
  box-shadow: 0 18px 45px rgba(15, 23, 42, 0.35);
}

.preview-card h2 {
  margin-top: 0;
  margin-bottom: 8px;
  font-size: 16px;
  font-weight: 700;
}

.preview-card p {
  margin: 0;
  font-size: 14px;
  line-height: 1.5;
}

.theme-toggle {
  display: inline-flex;
  align-items: center;
  gap: 8px;
  padding: 6px 12px;
  border-radius: 999px;
  border: 1px solid #e5e7eb;
  background: #eef2ff;
  color: #020617;
  cursor: pointer;
  font-size: 13px;
  font-weight: 600;
  transition: transform 120ms ease, box-shadow 120ms ease;
}

.theme-toggle .icon {
  font-size: 16px;
}

.theme-toggle:hover {
  transform: translateY(-1px);
  box-shadow: 0 10px 25px rgba(15, 23, 42, 0.3);
}

html[data-theme='dark'] .app-shell {
  background: #020617;
  color: #e5e7eb;
}

html[data-theme='dark'] .preview-card {
  background: #020617;
  border-color: #1f2937;
  box-shadow: 0 18px 45px rgba(15, 23, 42, 0.75);
}

html[data-theme='dark'] .theme-toggle {
  border-color: #1f2937;
  background: #111827;
  color: #e5e7eb;
}
