solution:
export default function mean(array) {
  if (array.length === 0) return NaN;
  return array.reduce((s, v) => s + v, 0) / array.length;
}

tests:
import mean from './mean';

describe('mean', () => {
  test('empty input array', () => {
    expect(mean([])).toBeNaN();
  });

  test('single value', () => {
    expect(mean([0])).toEqual(0);
  });

  test('two values', () => {
    expect(mean([1, 3])).toEqual(2);
  });
});
